public class Dowhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=0; // declare i=0
		System.out.println("First 10 even Numbers\n");
		do {
			System.out.println(i);
			i=i+2; //increment 
		}while(i<=10); //number of execution

	}

}
