public class Forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=0; //initializing i=0
		for(int j=1;j<=10;j++) //set j=1
		{
			i=i+j;	//logic for sum
		}
		System.out.println("Sum of First 10 Natural Number is "+i);
	}

}
