public class Ifelseladder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name="Riya"; //initializing the string
		if (name=="Meena") //checking condition is there string is Meena or not
			System.out.println("name is Meena");
		else if(name=="Neha") //checking condition is there string is Neha or not
			System.out.println("name is Neha");
		else if(name=="Amit") //checking condition is there string is Amit or not
			System.out.println("name is Amit");
		else
			System.out.println(name);
			

	}

}
